const Settings = ({ handleSignOut, handleProfileImg, selectedImage,  }) => {
  const handleProfileImgSubmit = async (e) => {
    e.preventDefault();

    if (selectedImage) {
      try {
        // Upload the image to Firebase Storage
        const imageFile = selectedImage;
        const storageRef = ref(storage, `${usernameInput}/profileImg/${imageFile.name}`);
        await uploadBytes(storageRef, imageFile);

        // Get the download URL for the uploaded image
        const downloadURL = await getDownloadURL(storageRef);

        // Update the user's profile image in Firestore
        await updateProfileImage(usernameInput, downloadURL);

        // Update the userImg state to display the new image
        setUserImg(downloadURL);
      } catch (error) {
        console.error('Error uploading image:', error);
      }
    }
  };

  const updateProfileImage = async (usernameInput, downloadURL) => {
    try {
      const usersRef = collection(database, 'users');
      const querySnapshot = await getDocs(query(usersRef, where('username', '==', usernameInput)));

      if (!querySnapshot.empty) {
        querySnapshot.forEach(async (doc) => {
          const userRef = doc.ref;
          await updateDoc(userRef, { profileImg: downloadURL }); // Update the 'profileImg' field
        });
      }
    } catch (error) {
      console.error('Error updating profile image:', error);
    }
  };

  return (
      <main className="Settings">
        <h2 className="settings-title">Settings</h2>
        <button className="settings-logout" onClick={handleSignOut}>Logout</button>
        <p style={{ marginTop: "1rem" }}></p>
        <form className="newPostForm" onSubmit={handleProfileImgSubmit}>

        <label htmlFor="imageInput">Image:</label>
                <input
                    id="imageInput"
                    type="file"
                    accept="image/*"
                />
               {selectedImage && <img src={selectedImage} alt="Selected" />}
               <button type="submit">Submit</button>


        </form>

      </main>
    );
  };

  export default Settings;
