import React, { useState } from 'react';
import { Avatar, Button } from '@mui/material';
import { Link } from 'react-router-dom';
import MyPostFeed from './MyPostFeed';
import './MyPostDisplay.css'; // Import the CSS file

const MyPostDisplay = ({ myPost, usernameInput, userImg, name, bio, setEditingProfile }) => {
  const [activeTab, setActiveTab] = useState('post'); // Default active tab is "post"
  const name2 = usernameInput;
  const handleTabChange = (tab) => {
    setActiveTab(tab);
  };

  return (
    <main className="Home">
      <div className="MyPostDisplay"> {/* Apply CSS class */}
        <div className="MyPostDisplay-header"> {/* Apply CSS class */}
          <Avatar
            src={userImg}
            sx={{ width: 60, height: 60 }}
            alt="Profile Img"
            className="MyPostDisplay-header-avatar"
          />
          {usernameInput}

          <div className="MyPostDisplay-buttons"> {/* Apply CSS class */}
            <Button
              color="primary"
              onClick={() => handleTabChange('post')}
              className={`MyPostDisplay-button ${activeTab === 'post' ? 'MyPostDisplay-button-active' : ''}`} 
            >
              Posts
            </Button>
            <Button
              color="primary"
              onClick={() => handleTabChange('followers')}
              className={`MyPostDisplay-button ${activeTab === 'followers' ? 'MyPostDisplay-button-active' : ''}`} 
            >
              Followers
            </Button>
            <Button
              color="primary"
              onClick={() => handleTabChange('following')}
              className={`MyPostDisplay-button ${activeTab === 'following' ? 'MyPostDisplay-button-active' : ''}`}
            >
              Following
            </Button>
          </div>
        </div>
        <div className="MyPostDisplay-content"> {/* Apply CSS class */}
          <div className="MyPostDisplay-name">{!name.length ? name2 : name}</div> {/* Apply CSS class */}
          <div className="MyPostDisplay-bio">{bio}</div> {/* Apply CSS class */}
        </div>
        <Button
          className="MyPostDisplay-editButton"
        >
          <Link onClick={setEditingProfile} to="editProfile" style={{ color: 'black', textDecoration: 'none' }}>
            Edit Profile
          </Link>
        </Button>
      </div>

      {activeTab === 'post' ? (
        myPost.length ? <MyPostFeed myPost={myPost} /> : <p style={{ marginTop: '2rem' }}>No posts to display.</p>
      ) : (
        <p>Content for {activeTab} tab goes here.</p>
      )}
    </main>
  );
};

export default MyPostDisplay;
