import { initializeApp } from "firebase/app";
import { getAuth } from "firebase/auth";
import { getFirestore } from "firebase/firestore"; // Import getFirestore only once

// Firebase config
const firebaseConfig = {
  apiKey: process.env.API_KEY,
  authDomain: "mj-blog-59366.firebaseapp.com",
  projectId: "mj-blog-59366",
  storageBucket: "mj-blog-59366.appspot.com",
  messagingSenderId: "771595840572",
  appId: "1:771595840572:web:dc657c901c6bd7dd592739"
};
// initialize firebase
const app = initializeApp(firebaseConfig);

// Export auth and database instances
export const auth = getAuth(app);
export const database = getFirestore(app);
