const Post = ({ post, usernameInput, setProfile }) => {
  return (
    <article className="post">
      <Link to={`/post/${post.id}`}>
        <h2>
          <Link
            to={
              post.username === usernameInput
                ? '/myPost'
                : `/profile/${post.username}`
            }
            onClick={() => setProfile(post.username)} // Call setProfile here
          >
            <Avatar
              style={{ marginRight: 10 }}
              src={
                post.profileImg === undefined ? 'user.png' : post.profileImg
              }
              alt={post.username}
            />
          </Link>
          {post.title} By: {post.username}
        </h2>
        <p className="postDate">{post.datetime}</p>
      </Link>
      <p className="postBody">
        {post.body.length <= 25
          ? `${post.body} `
          : `${post.body.slice(0, 25)}...`}
      </p>
    </article>
  );
};
