import React from 'react';
import { Link } from 'react-router-dom';

const Post = ({ post }) => {
  return (
    <article className="post" key={post.firebaseTag}>
      <Link to={`/post/${post.firebaseTag}`}>
        <h2>{post.title}</h2>
        <p className="postDate">{post.datetime}</p>
      </Link>
      <p className="postBody">
        {(post.body).length <= 25
          ? `${post.body} ${post.username}`
          : `${(post.body).slice(0, 25)}...${post.username}`}
      </p>
    </article>
  );
};

export default Post;
